mapper.select:

	select no, title, content, hit, user_no 
	from replyboard 
	where no = #{no}


	select title, content, no, user_no
	from replyboard
	where no = #{no}

mapper.update:
	
		update replyboard
		set title = #{title},
		content = #{content}
		where no = #{no}

	
		update replyboard
		set hit = hit+1
		where no = #{no}

	
		update replyboard
		set order_no = order_no+1
		where group_no = #{groupNo}
		and order_no > #{orderNo}
